import React, { useState, useEffect } from 'react'
import personService from './services/persons'

//TODO: match case for name input
//TODO: reject submit if number or name has no input
//TODO: create new files for components (separate or joined?)

const Person = (props) =>   
  <div>
    {props.name} {props.number}
    <button onClick={props.onClick}>
    delete
  </button>
  </div>


const Persons = ({array, onClick}) =>
  <div>
    {array.map(
    (person) => <Person key={person.id} name = {person.name} number={person.number} onClick={() => onClick(person.id)}/>
    )}
  </div>

const PersonsForm = (props) =>
  <form onSubmit = {props.onSubmit}>
    <div>name: <input value = {props.valueName} onChange = {props.onChangeName}/></div>
    <div>number: <input value = {props.valueNumber} onChange = {props.onChangeNumber}/></div>
    <div> <button type="submit">add</button> </div>
  </form>

const Filter = (props) =>
  <form>
    {props.text} <input value = {props.value} onChange = {props.onChange}/>
  </form>

const App = () => {
  const [ persons, setPersons ] = useState([])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber] = useState('')
  const [ newFilter, setNewFilter] = useState('')
  const handleNameChange = event => setNewName(event.target.value)
  const handleNumberChange = event => setNewNumber(event.target.value)
  const handleFilterChange = event => setNewFilter(event.target.value)

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])

  const addPerson = event => {
    event.preventDefault()
    const personsNames = persons.map(person => person.name) 
    if (personsNames.includes(newName)) {                   
      if (window.confirm(`${newName} is already added to phonebook, replace the old number with a new one?`)) {
        console.log(newName)
      }
    }
    else {
      const personObject = {
        name: newName,
        number: newNumber
      }

      personService
        .create(personObject)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
          setNewName('')
          setNewNumber('')
        })
    }
  }

  const updateNumber = name => { //Technically, one
    const person = persons.find(n => n.name === name)
  }

  const deletePerson = id => {
    const person = persons.find(n => n.id === id)

    if (window.confirm(`delete ${person.name} ?`)) {
      personService
      .erase(id)
      .then(newPersons => {
        setPersons(persons.filter(item => item.id !== person.id))
      })
    }
  }

 const personsFiltered = (newFilter === '')
 ? persons
 : persons.filter(
   v => v.name.toLowerCase().includes((newFilter).toLowerCase()) //toLowerCase() is used to handle
   )  // insensitive case problems by lowercasing both the 'name' property and the user's input

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter text = {'filter shown with'} value={newFilter} onChange={handleFilterChange}/>

      <h2>add a new</h2>
      <PersonsForm
      onSubmit = {addPerson}
      valueName = {newName} onChangeName = {handleNameChange}
      valueNumber = {newNumber} onChangeNumber={handleNumberChange}/>

      <h2>Numbers</h2>
      <Persons array = {personsFiltered} onClick={deletePerson}/>
    </div>
  )
}

export default App
