import React, { useState } from 'react'

const Persons = (props) =>   
  <div>{props.name} {props.number}</div>

const PersonsForm = (props) =>
  <form onSubmit = {props.onSubmit}>
    {props.text} <input value = {props.value} onChange = {props.onChange} />
    <PersonsForm text = {'name: '} value={newName} onChange={handleNameChange}/>
        <PersonsForm text = {'number: '} value={newNumber} onChange={handleNumberChange}/>
  </form>

const Filter = (props) =>
  <form>
    {props.text} <input value = {props.value} onChange = {props.onChange} />
  </form>

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])

  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber] = useState('')
  const [ newFilter, setNewFilter] = useState('')

  const addPerson = event => {
    event.preventDefault()
    const personsNames = persons.map(person => person.name)
    if (personsNames.includes(newName)) {
      alert(`${newName} is already added to phonebook`)
    }
    else {
      const personObject = {
        name: newName,
        number: newNumber
      }

      setPersons(persons.concat(personObject))
      setNewName('')
    }
  }

  const handleNameChange = event => setNewName(event.target.value)
  const handleNumberChange = event => setNewNumber(event.target.value)
  const handleFilterChange = event => setNewFilter(event.target.value)

  const personsFiltered = (newFilter === '') 
  ? persons 
  : persons.filter(v => v.name.toLowerCase().includes((newFilter).toLowerCase())) //toLowerCase() is used to handle insensitive case problems

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <Filter text = {'filter shown with'} value={newFilter} onChange={handleFilterChange}/>
      </form>

      <h2>add a new</h2>
      <form onSubmit={addPerson}>
        <PersonsForm text = {'name: '} value={newName} onChange={handleNameChange}/>
        <PersonsForm text = {'number: '} value={newNumber} onChange={handleNumberChange}/>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {personsFiltered.map((person,index) => <Persons key={index} name = {person.name} number={person.number}/>)}
    </div>
  )
}

export default App
